package io.jiantao.android.uikit.util;import android.graphics.Rect;import android.view.View;import androidx.core.view.ViewCompat;/** * Created by Jiantao.Yang on 2019-12-18. */public class ViewUtils {    /**     * 判断view是否显示在屏幕上。 但不能判断被上层视图遮盖的情况。     * 参考：https://juejin.im/entry/5dae45f8f265da5ba46f6106 、 https://www.jianshu.com/p/30b0ae304518     *     * @return true -> yes, false -> no     */    public static boolean isVisibleToUser(View view) {        if (view == null || !view.isShown()) {            return false;        }        Rect tempVisibleRect = new Rect();        return ViewCompat.isAttachedToWindow(view) && view.getGlobalVisibleRect(tempVisibleRect);    }}